mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT o.orders_num, o.orders_date, o.sn_name, o.orders_total FROM member m JOIN orders o ON m.mem_num = o.mem_num WHERE o.mem_num=#{user}
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT r.review_num, r.review_date, r.review_content, r.rating_score FROM member m JOIN review r ON m.mem_num = r.mem_num WHERE r.mem_num=#{user}
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT p.post_num, p.post_date, p.post_content, p.post_photo FROM member m JOIN moviepost p ON m.mem_num = p.mem_num WHERE p.mem_num=#{user}
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT * FROM member m LEFT OUTER JOIN member_detail d ON m.mem_num = d.mem_num
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT p.personal_num, p.title, p.content, p.state FROM cs_personal p JOIN member m ON p.mem_num = m.mem_num WHERE p.mem_num=#{mem_num}
)a)
WHERE rnum >= #{start} AND rnum <= #{end}

SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT e.title, e.event_num FROM event e JOIN event_reply er ON er.event_num = e.event_num JOIN member m ON er.mem_num = m.mem_num WHERE er.mem_num=#{mem_num}
)a)
WHERE rnum >= #{start} AND rnum <= #{end}
