mapper.select:
SELECT * FROM (SELECT a.*, rownum rnum FROM(SELECT s.sch_num, m.m_title, th.th_name, h.hall_name, s.sch_date, s.sch_start, s.sch_end FROM schedule s JOIN hall h ON s.hall_num = h.hall_num JOIN movie m ON s.movie_num = m.movie_num JOIN theater th ON h.th_num = th.th_num
ORDER BY s.sch_date)a)
WHERE rnum >= #{start} AND rnum <= #{end}
