mapper.select:
SELECT * 
FROM (SELECT a.*, rownum rnum 
	  FROM (SELECT * 
	  		FROM snack 

	  		)a) 

WHERE rnum >= #{start} AND rnum <= #{end}

mapper.update:
UPDATE snack SET sn_name=#{sn_name}, sn_detail=#{sn_detail}, sn_price=#{sn_price}, sn_dc_price=#{sn_dc_price},
sn_photo=#{sn_photo}, sn_photo_name=#{sn_photo_name},
sn_info=#{sn_info}, sn_category=#{sn_category}, sn_modify_date=SYSDATE, sn_status=#{sn_status} WHERE sn_num=#{sn_num}

mapper.delete:
DELETE FROM snack 
WHERE sn_num IN 

	#{item}
